INSERT OR IGNORE INTO success_stories (region_id, title, author, age_group, previous_job, settlement_year, business_type, annual_revenue, content, images) VALUES 
  (1, '서울 직장인에서 마늘 농부로 변신', '김대한', '30대', 'IT 개발자', 2021, '농업', 8500, '10년간 IT 업계에서 일하다 번아웃을 느끼고 귀농을 결심했습니다. 의성군의 청년 귀농 패키지 지원을 받아 5000평의 마늘밭을 시작했고, 3년 차인 지금은 연 8500만원의 수익을 올리고 있습니다. 스마트팜 기술을 접목해 작업 효율을 높였고, 온라인 직거래로 마진을 개선했습니다.', '["https://via.placeholder.com/400x300"]'),
  (2, '은퇴 후 제2의 인생, 청송 사과농장', '이영숙', '50대', '중학교 교사', 2020, '농업', 12000, '30년간 교직 생활을 마치고 남편과 함께 청송으로 귀농했습니다. 스마트팜 교육을 이수하고 3000평 규모의 사과농장을 운영 중입니다. 관광농업과 체험프로그램을 병행하며 연 1억 2천만원의 매출을 달성하고 있습니다.', '["https://via.placeholder.com/400x300"]'),
  (3, '유자 가공으로 연매출 2억 달성', '박진수', '40대', '요식업', 2019, '가공업', 20000, '서울에서 레스토랑을 운영하다 고흥의 청정 유자에 매료되어 귀농했습니다. 유자 재배와 함께 유자청, 유자차, 유자즙 등 가공사업을 시작했고, 온라인 판매와 대형마트 입점으로 5년 만에 연 2억원의 매출을 올리는 기업으로 성장했습니다.', '["https://via.placeholder.com/400x300"]');
